mapper.select:

 select * from event where 1=1


	 and event_num = #{eventNum} 

	 and company_id = #{companyId} 

	 and title like '%' || #{eventTitle}||'%'  

	 and content like '%' || #{eventContent}||'%' 

	 and create_date = #{eveCreDate} 

	 and start_date = #{eveStartDate} 

	 and end_date = #{eveEndDate}
