mapper.update:
 	update room 
	set room_number=#{roomNumber}
		,accom_name=#{accomName}
		,company_id=#{companyId}

		,grade=#{grade}


		,price=#{price}


		,appropriate_num=#{appropriateNum}


		,description=#{description}

	where room_number=#{roomNumber}

mapper.select:
select *
from room
where 1=1

	and room_number=#{roomNumber}


	and accom_name=#{accomName}


	and company_id=#{companyId}


	and company_id=#{companyId}


	and grade like '%' || #{grade} || '%'


	and price like '%' || #{price} || '%'


	and appropriate_num like '%' || #{appropriateNum} || '%'


	and description like '%' || #{description} || '%'

select *
from room
where grade <=#{maxGrade} and grade >= #{minGrade}

select *
from room
where price <=#{maxPrice} and grade >= #{minPrice}

select *
from room
where appropriate_num <=#{maxAppro} and appropriate_num >= #{minAppro}
